function () {
  const today = new Date();
  const options = {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit'
  };
  const formattedDate = today.toLocaleDateString('vi-VN', options);
  return formattedDate.split('/').reverse().join('/');
}

