    manager = record[0].id;
  }
  let idTaskParent = appsmith.URL.queryParams.key ? [parseInt(appsmith.URL.queryParams.key)] : "";
  const task = {
    "Ten_Cong_Viec": taskName,
    "Mo_Ta": description,
    "Trang_Thai": "Active",
    "Ngay_Bat_Dau": dateStarted,
    "Ngay_Ket_Thuc": deadline,
    "Nguoi_Quan_Ly": [manager],
    "Cong_Viec_Cha": idTaskParent
  };
  return task;
}

